let minConfidence = {MinConfidence};
let filter = "{IndicatorFilter}";

if ("{SourceTable}" == "CTI_StixData_CL") {
    CTI_StixData_CL
    | where TimeGenerated {TimeRange}
    | extend STIXType = tolower(STIXType)
    | where STIXType in ("indicator", "threat-actor", "malware", "attack-pattern")
    | where isempty(filter) or RawSTIX contains filter
    | extend IndicatorId = STIXId
    | project TimeGenerated, Source, STIXType, STIXId, RawSTIX, CreatedBy
    | order by TimeGenerated desc
    | take 500
}
else {
    CTI_ThreatIntelIndicator_CL
    | where TimeGenerated {TimeRange}
    | where isempty(filter) or Value_s contains filter
    | where isempty(Confidence_d) or Confidence_d >= minConfidence
    | project TimeGenerated, Type_s, Value_s, Pattern_s, Name_s, Description_s, Confidence_d, Source_s, ValidFrom_t, ValidUntil_t, IndicatorId_g, Active_b
    | order by TimeGenerated desc
    | take 500
}

