// Generate a GUID for the indicator
let indicatorId = guid();
let now = now();
let expirationDate = now + {ExpirationDays}d;
let distributionTargets = "{Distribution}";

// Build messages based on the indicator type
print ProcessingMessage = strcat("Processing indicator of type '{TargetType}': {SelectedValue}");

print TargetTable = case(
    "{TargetType}" == "IP", "CTI_IPIndicators_CL",
    "{TargetType}" == "Domain", "CTI_DomainIndicators_CL",
    "{TargetType}" == "URL", "CTI_URLIndicators_CL",
    "{TargetType}" == "FileHash", "CTI_FileHashIndicators_CL",
    "Unknown");

// Data function would be implemented here in a production workbook
print ActionResult = strcat("The indicator is being processed and will be added to ", TargetTable);
print ActionNotes = "In a production implementation, the appropriate _Data_PutBlob would be called here with all the collected metadata.";

print DistributionStatus = strcat("This indicator will be distributed to: ", distributionTargets);

print ExpirationInfo = strcat("Indicator will expire on: ", expirationDate);

// Generate a GUID for the new feed
let feedId = guid();
let now = now();

// Extract values from parameters
let feedType = "{NewFeedType}";
let feedName = "{NewFeedName}";
let feedURL = "{NewFeedURL}";
let username = "{NewFeedUsername}";
let password = "{NewFeedPassword}";
let isActive = {NewFeedActive};
let refreshInterval = {NewFeedRefreshInterval};
let description = "{NewFeedDescription}";
let tlp = "{NewFeedTLP}";
let distribution = "{NewFeedDistribution}";
let config = "{NewFeedConfiguration}";

// Validate input
let validationResult = case(
    isempty(feedName), "Feed name is required",
    isempty(feedURL), "Feed URL is required",
    refreshInterval !between (1 .. 168), "Refresh interval must be between 1 and 168 hours",
    "Valid"
);

// Show validation result
print ValidationResult = validationResult;

// If valid, show what would be inserted
if (validationResult == "Valid") {
    print SuccessMessage = strcat("Adding new ", feedType, " feed: ", feedName);
    
    // This would be the data to insert in a real implementation
    print FeedData = strcat(
        "FeedId_g: ", feedId, ", ",
        "FeedType_s: ", feedType, ", ",
        "FeedName_s: ", feedName, ", ",
        "FeedURL_s: ", feedURL, ", ",
        "RefreshInterval_d: ", refreshInterval, ", ",
        "LastUpdateTime_t: ", now, ", ",
        "Description_s: ", description, ", ",
        "DefaultTLP_s: ", tlp, ", ",
        "DistributionTargets_s: ", distribution, ", ",
        "ConfigData_s: ", config, ", ",
        "Active_b: ", isActive
    );
    
    // In a real implementation, this would be _Data_PutBlob to CTI_IntelligenceFeeds_CL
    print ImplementationNote = "In a production workbook, _Data_PutBlob would be used to insert the data into CTI_IntelligenceFeeds_CL";
}

// Simulate saving the distribution configuration
let target = "{DistributionTarget}";
let autoApprove = {AutoApprove};
let minConfidence = {MinimumConfidence};
let allowedTypes = "{AllowedTypes}";
let syncFrequency = {SyncFrequency};
let defaultAction = "{DefaultAction}";
let now = now();

// Build a record of the configuration
let configData = strcat(
    "Target: ", target, ", ",
    "AutoApprove: ", autoApprove, ", ",
    "MinimumConfidence: ", minConfidence, ", ",
    "AllowedTypes: ", allowedTypes, ", ",
    "SyncFrequency: ", syncFrequency, ", ",
    "DefaultAction: ", defaultAction, ", ",
    "LastUpdated: ", now
);

print Status = "Configuration saved successfully";
print ConfigSaved = configData;
print NextSteps = "The distribution configuration has been updated. The changes will take effect at the next scheduled sync.";
print Implementation = "In a production environment, this would save the configuration to a table that the Logic Apps would reference.";

// System health status
// Logic App run status
let logicApps = dynamic(["CTI-TAXII2-Connector", "CTI-DefenderXDR-Connector", "CTI-MDTI-Connector", "CTI-EntraID-Connector", "CTI-ExchangeOnline-Connector", "CTI-SecurityCopilot-Connector", "CTI-Housekeeping", "CTI-ThreatFeedSync"]);

// Get Logic App runs status
let workflowRuns = union 
    // Get recent workflow runs
    (workspace("*").WorkflowRuntime
    | where TimeGenerated > ago(24h)
    | where ResourceId contains "Microsoft.Logic/workflows" and Status in ("Failed", "Succeeded")
    | extend ResourceName = tostring(split(ResourceId, "/")[8])
    | extend isLogicApp = indexof(logicApps, ResourceName) != -1
    | where isLogicApp
    | project TimeGenerated, ResourceName, Status, startTime_t);

// Process workflow status
workflowRuns
| summarize LastRunStatus = arg_max(TimeGenerated, Status) by ResourceName
| extend Status = iff(LastRunStatus == "Failed", "Failed", "Healthy")
| project Component = ResourceName, Status
| union (
    // Add a row for workspace status - always healthy if query runs
    datatable(Component:string, Status:string)[ "Log Analytics Workspace", "Healthy"]
)
| union (
    // Add CTI tables status
    union 
    (CTI_IPIndicators_CL | take 1 | extend TableName = "CTI_IPIndicators_CL"),
    (CTI_DomainIndicators_CL | take 1 | extend TableName = "CTI_DomainIndicators_CL"),
    (CTI_URLIndicators_CL | take 1 | extend TableName = "CTI_URLIndicators_CL"),
    (CTI_FileHashIndicators_CL | take 1 | extend TableName = "CTI_FileHashIndicators_CL")
    | summarize Count = count() by TableName
    | extend Status = "Healthy"
    | project Component = TableName, Status
)
| order by Component asc

// Logic App run history
let logicApps = dynamic(["CTI-TAXII2-Connector", "CTI-DefenderXDR-Connector", "CTI-MDTI-Connector", "CTI-EntraID-Connector", "CTI-ExchangeOnline-Connector", "CTI-SecurityCopilot-Connector", "CTI-Housekeeping", "CTI-ThreatFeedSync"]);

// Get Logic App runs
workspace("*").WorkflowRuntime
| where TimeGenerated {HealthTimeRange}
| where ResourceId contains "Microsoft.Logic/workflows"
| extend ResourceName = tostring(split(ResourceId, "/")[8])
| extend isLogicApp = indexof(logicApps, ResourceName) != -1
| where isLogicApp
| project TimeGenerated, Connector = ResourceName, Status, RunId = WorkflowRunId
| order by TimeGenerated desc

