// System diagnostics
print DiagnosticResult = "Running system diagnostics...";

// Table counts
let ipCount = toscalar(CTI_IPIndicators_CL | summarize count());
let domainCount = toscalar(CTI_DomainIndicators_CL | summarize count());
let urlCount = toscalar(CTI_URLIndicators_CL | summarize count());
let fileHashCount = toscalar(CTI_FileHashIndicators_CL | summarize count());
let totalCount = ipCount + domainCount + urlCount + fileHashCount;

// Active indicator counts
let activeIPCount = toscalar(CTI_IPIndicators_CL | where Active_b == true | summarize count());
let activeDomainCount = toscalar(CTI_DomainIndicators_CL | where Active_b == true | summarize count());
let activeURLCount = toscalar(CTI_URLIndicators_CL | where Active_b == true | summarize count());
let activeFileHashCount = toscalar(CTI_FileHashIndicators_CL | where Active_b == true | summarize count());
let totalActiveCount = activeIPCount + activeDomainCount + activeURLCount + activeFileHashCount;

// System health assessment
let healthStatus = "Healthy";
let healthIssues = dynamic([]);

// Print diagnostics results
print TotalIndicators = strcat("Total Indicators: ", totalCount, " (Active: ", totalActiveCount, ")");
print TableCounts = strcat(
    "IP Addresses: ", ipCount, " (Active: ", activeIPCount, ")", 
    ", Domains: ", domainCount, " (Active: ", activeDomainCount, ")",
    ", URLs: ", urlCount, " (Active: ", activeURLCount, ")",
    ", File Hashes: ", fileHashCount, " (Active: ", activeFileHashCount, ")"  
);

print SystemHealth = strcat("Overall System Health: ", healthStatus);
print Recommendations = "Regular maintenance is recommended to ensure optimal performance. Consider removing expired indicators more than 90 days old.";
